        &lt;transactionManager type="JDBC"&gt;&lt;/transactionManager&gt;

        &lt;!--dataSource标签就是用来配置数据库连接信息 POOLED|UNPOOLED --&gt;
        &lt;dataSource type="POOLED"&gt;
